    componentDidMount() {
        const db = firebase.database();
        console.log(db)
    }

    handleChange = ({ target: {value, id }}) => {
        console.log(value)
    }; 

    uploadedPhoto = (value) => {
      console.log(value)
      this.setState({url : value.cdnUrl})
    }


    render() {
      if (this.state.isLoggedIn) {
      return (
          <div>
            <Widget onChange={this.uploadedPhoto} id="gotovofoto" publicKey="30506db15e8554c1cad3" />
            <p>{this.state.url}</p>
                <FoodList />
          </div>
        )} else {
          return (
            <div>
              <AuthForm />
            </div>)
        }
    }
};
